# https://www.hackerrank.com/challenges/string-function-calculation/problem
from collections import defaultdict

maps = defaultdict(int) 
def maxValueUtil(t, index):
	if index == len(t) :
		return
	for i in range(1, len(t) -index) : 
		sub = 	t[index:index + i + 1]
		maps[sub] += 1

	maxValueUtil(t, index + 1)

def maxValue(t) :
	
	index = 0 
	while index < len(t) :
		for i in range(1, len(t) -index) : 
			sub = 	t[index:index + i + 1]
			maps[sub] += 1
		index += 1

	maxval = 0
	maxs = "" 
	for s in list(maps.keys()) :
		val = len(s) * maps[s]
		if val > maxval :
			maxval = val
			maxs = s
	print(maxs)
	return maxval

